import React from 'react'
import { useState } from 'react';
import {
    Button,
    Modal,
    Alert,
    Form,
    Container
  } from "react-bootstrap";

export default function Login({hide, show}) {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [errorMessage, setErrorMessage] = useState(null);
    const [formValid, setFormValid] = useState(true)
    const handleValidation = () => {
        //Password
        if(!password){
            setErrorMessage("Password cannot be empty");
            setFormValid(false);
        }

        //Email
        if(!email){
          setErrorMessage("Email cannot be empty");
          setFormValid(false);
        }
        if(typeof email !== "undefined"){
          let lastAtPos = email.lastIndexOf('@');
          let lastDotPos = email.lastIndexOf('.');
          if (!(lastAtPos < lastDotPos && lastAtPos > 0 && email.indexOf('@@') === -1 && lastDotPos > 2 && (email.length - lastDotPos) > 2)) {
            setErrorMessage("Email is not valid");
            setFormValid(false);
          }
        }
    }

    const submitHandler = () => {
        if(handleValidation()){
            hide(false);
            console.log(email,password)
        }
    }
    return (
        <Modal show={show}
            size="lg"
            aria-labelledby="contained-modal-title-vcenter"
            centered
            >
            <Modal.Header className="d-flex align-items-center justify-content-center">
                <Modal.Title id="contained-modal-title-vcenter">
                Login
                </Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Container>
                <Form>
                    <Form.Group className="mb-3" controlId="formGroupEmail">
                        <Form.Label>Email address</Form.Label>
                        <Form.Control type="email" placeholder="Enter email" onChange={e => {
                                    e.preventDefault();
                                    setEmail(e.target.value)
                                }}/>
                    </Form.Group>
                    <Form.Group className="mb-3" controlId="formGroupPassword">
                        <Form.Label>Password</Form.Label>
                        <Form.Control type="password" placeholder="Password" onChange={e => {
                                    e.preventDefault();
                                    setPassword(e.target.value)
                                }}/>
                    </Form.Group>
                    {
                        errorMessage && <Alert variant={'danger'}>
                            {errorMessage}
                        </Alert>
                    }
                </Form>
                </Container>
            </Modal.Body>
            <Modal.Footer>     
                <Button type="submit" className="btn btn-danger btn-lg btn-block" onClick={() => hide(false)}>Cancel</Button>
                <Button type="submit" className="btn btn-primary btn-lg btn-block" onClick={() => submitHandler()}>Sign in</Button>
            </Modal.Footer>
            </Modal>
    )
}
