import React, { useContext } from 'react'
import { Link } from "react-router-dom";
import { Context } from '../context/Context';
import { Dropdown, Image } from 'react-bootstrap';
import logo from '../assets/img/scorp-logo.png';

import i18n from '../i18n';
import { withNamespaces } from 'react-i18next';

function Header({t, setLoginModalShow}) {
    const {userInfo, setUserInfo} = useContext(Context);

    const logOutHandler = () => {
        setUserInfo({email:'', name:'', locale:'', password:''});
    }

    const changeLanguage = (lng) => {
        i18n.changeLanguage(lng);
      }

    return (
    <>
    <header className="header">
        <nav className="navbar navbar-expand-lg navbar-dark navbar-custom fixed-top">
            <div className="container px-5 my-1">
            <Image src={logo} thumbnail height={60} width={60} className="mr-3"/>
            <Link className="navbar-brand" to="/">Scorp App</Link>
            <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarResponsive" aria-controls="navbarResponsive" aria-expanded="false" aria-label="Toggle navigation"><span className="navbar-toggler-icon"></span></button>
            <div className="collapse navbar-collapse" id="navbarResponsive">
                <ul className="navbar-nav ms-auto">
                    <li className="nav-item">
                        <Link className="nav-link" to="/contact-us">{t('Contact Us')}</Link>
                    </li>
                    <Dropdown className="d-inline mx-2">
                        <Dropdown.Toggle id="dropdown-autoclose-true">{t('Lang')}
                        </Dropdown.Toggle>
                        <Dropdown.Menu className="mx-2">
                            <Dropdown.Item onClick={() => changeLanguage('en')}>
                            {t('English')}
                            </Dropdown.Item>
                            <Dropdown.Item onClick={() => changeLanguage('tr')}>
                            {t('Turkish')}
                            </Dropdown.Item>
                        </Dropdown.Menu>
                    </Dropdown>
                    {
                        userInfo?.name ?
                        <>
                        <Dropdown className="d-inline mx-2">
                        <Dropdown.Toggle id="dropdown-autoclose-true">
                        {userInfo?.email}
                        </Dropdown.Toggle>
                        <Dropdown.Menu className="mx-2">
                            <Dropdown.Item onClick={() => logOutHandler()}>
                            {t('Log Out')}
                            </Dropdown.Item>
                        </Dropdown.Menu>
                    </Dropdown>
                        </> : <li className="nav-item">
                            <button className="btn btn-secondary nav-link" onClick={() => setLoginModalShow(true)}>{t('Log In')}</button>
                    </li>
                    }
                </ul>
            </div>
        </div>
        </nav>
        </header>
    </>)
}

export default withNamespaces()(Header);
