import React, { useState, useContext }  from 'react'
import { Container,Form, Row, Col, Button, Alert, FloatingLabel } from 'react-bootstrap';
import { Context } from '../context/Context';
import { countryList } from '../data/countryList';
import Select from 'react-select'

export default function ContactUsPage() {

    const {userInfo, setUserInfo} = useContext(Context);
    const [formData, setFormData] = useState({name:null,email:null,phonenumber:null,country_code:null,text:null});

    const [validated, setValidated] = useState(false);

    const handleSubmit = (event) => {
      const form = event.currentTarget;
      if (form.checkValidity() === false) {
        event.preventDefault();
        event.stopPropagation();
      }

      setValidated(true);
      console.log("girdi")
      console.log(formData)
    };

    return (
        <Container className="container my-5 w-100 d-flex justify-content-center align-items-center">
            <Col xs={12} md={8}>
            <h1>Contact Us Form</h1>
                <Row className="d-flex justify-content-center align-items-center">
                    <Form className="w-100" noValidate validated={validated} onSubmit={handleSubmit}>
                    <Form.Group className="mb-2" controlId="formGroupName">
                        <Form.Label>Name</Form.Label>
                        <Form.Control
                            required
                            type="text" 
                            placeholder="Name" 
                            value={userInfo?.name} onChange={e => 
                                {
                                    e.preventDefault();
                                    setFormData({...formData,name:e.target.value});
                                }}
                                />
                    </Form.Group>
                    <Form.Group className="mb-2" controlId="formGroupEmail">
                        <Form.Label>Email</Form.Label>
                        <Form.Control 
                            required
                            type="email" 
                            value={userInfo?.email} 
                            placeholder="Enter email" 
                            onChange={e => {
                                    e.preventDefault();
                                    setFormData({...formData,email:e.target.value});
                                }}/>
                    </Form.Group>
                    <Form.Group className="mb-2" controlId="formGroupPhoneNumber">
                        <Form.Label>Phone Number</Form.Label>
                        <Form.Control 
                            required
                            type="tel"
                            placeholder="0545-678-9000"
                            size="11"
                            minLength="11" 
                            maxLength="11"
                            onChange={e => {
                                    e.preventDefault();
                                    setFormData({...formData,phonenumber:e.target.value});
                                }}/>
                    </Form.Group>
                    <Form.Group className="mb-2" controlId="formGroupLocale">
                        <Form.Label>Locale</Form.Label>
                        {/* <Form.Select 
                            formNoValidate
                            defaultValue={userInfo?.locale} 
                            onChange={e => {
                            e.preventDefault();
                                }}>
                            {countryList.map((country) => (
                                <option key={country.value} value={country.value}>{country.label}</option>
                            ))}
                        </Form.Select> */}

                        <Select 
                            options={countryList} 
                            onInputChange = { (newValue) => {
                                    const inputValue = newValue.replace(/\W/g, '');
                                    setFormData({...formData,country_code:inputValue});
                            }
                        }/>
                    </Form.Group>
                    <Form.Label>Text</Form.Label>
                    <FloatingLabel className="mb-2" controlId="floatingTextarea2" label="Message">
                        <Form.Control
                        formNoValidate
                        as="textarea"
                        style={{ height: '100px' }}
                        onChange={e => {
                            e.preventDefault();
                            setFormData({...formData,text:e.target.value});
                                }}/>
                    </FloatingLabel>
                    <Col xs="auto" className="d-flex my-1 justify-content-end">                    
                        <Button type="submit" className="btn btn-success  btn-block my-1 mb-3">Log in</Button>
                    </Col>
                </Form>
                </Row>
            </Col>        
        </Container>
            )
}
