import React from 'react'
import { useState } from 'react';
import {
    Button,
    Modal,
    Alert
  } from "react-bootstrap";

export default function Login({hide, show}) {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [errorMessage, setErrorMessage] = useState(null);

    const handleValidation = () => {

        //Email
        if(!email){
          setErrorMessage("Email cannot be empty");
        }
        if(typeof email !== "undefined"){
          let lastAtPos = email.lastIndexOf('@');
          let lastDotPos = email.lastIndexOf('.');
          if (!(lastAtPos < lastDotPos && lastAtPos > 0 && email.indexOf('@@') == -1 && lastDotPos > 2 && (email.length - lastDotPos) > 2)) {
            setErrorMessage("Email is not valid");
          }
        }
        //Password
        if(!password){
            setErrorMessage("Password cannot be empty");
          }
    }

    const submitHandler = () => {
        if(handleValidation()){
            alert("Form submitted");
            hide(false);
            console.log(email,password)
        }else{
            alert("Form has errors.")
        }
    }
    return (
        <Modal show={show}
            size="lg"
            aria-labelledby="contained-modal-title-vcenter"
            centered
            >
            <Modal.Header className="d-flex align-items-center justify-content-center">
                <Modal.Title id="contained-modal-title-vcenter">
                Login
                </Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <div className="comtainer d-flex align-items-center justify-content-center h-100">
                    <div className="row offset-xl-1">
                    <form>
                        <div className="form-outline mb-4">
                            <input type="email" id="email" className="form-control form-control-lg" 
                                onChange={e => {
                                    e.preventDefault();
                                    setEmail(e.target.value)
                                }}
                            />
                            <label className="form-label">Email address</label>
                        </div>
                        <div className="form-outline mb-4">
                            <input type="password" id="password" className="form-control form-control-lg" 
                                onChange={e => {
                                    e.preventDefault();
                                    setPassword(e.target.value)
                                }}
                            />
                            <label className="form-label">Password</label>
                        </div>
                    </form>
                    {
                        errorMessage && <Alert variant={'danger'}>
                            {errorMessage}
                        </Alert>
                    }

                    </div>
                </div>
            </Modal.Body>
            <Modal.Footer>     
                <Button type="submit" className="btn btn-danger btn-lg btn-block" onClick={() => hide(false)}>Cancel</Button>
                <Button type="submit" className="btn btn-primary btn-lg btn-block" onClick={() => submitHandler()}>Sign in</Button>
            </Modal.Footer>
            </Modal>
    )
}
