import React, { useState, useContext }  from 'react'
import { Container,Form, Row, Col, Button, Alert, FloatingLabel } from 'react-bootstrap';
import { Context } from '../context/Context';
import { countryList } from '../data/countryList';
export default function ContactUsPage() {

    const {userInfo, setUserInfo} = useContext(Context);
    const [errorMessage, setErrorMessage] = useState(null);
    return (
        <Container className="container my-4">
                <h1>Contact us Form</h1>
                <Row className="d-flex justify-content-center align-items-center col-md-8">
                    <Form className="w-100 ">
                    <Form.Group className="mb-2" controlId="formGroupName">
                        <Form.Label>Name</Form.Label>
                        <Form.Control type="text" placeholder="Name" value={userInfo?.name} onChange={e => {
                                    e.preventDefault();
                                }}/>
                    </Form.Group>
                    <Form.Group className="mb-2" controlId="formGroupEmail">
                        <Form.Label>Email</Form.Label>
                        <Form.Control type="email" value={userInfo?.email} placeholder="Enter email" onChange={e => {
                                    e.preventDefault();
                                }}/>
                    </Form.Group>
                    <Form.Group className="mb-2" controlId="formGroupPhoneNumber">
                        <Form.Label>Phone Number</Form.Label>
                        <Form.Control type="phone" placeholder="Enter phone" onChange={e => {
                                    e.preventDefault();
                                }}/>
                    </Form.Group>
                    <Form.Group className="mb-2" controlId="formGroupLocale">
                        <Form.Label>Locale</Form.Label>
                        <Form.Select 
                                    defaultValue={userInfo?.locale} 
                                    onChange={e => {
                                    e.preventDefault();
                                }}>
                            {countryList.map((country) => (
                                <option key={country.id} value={country.id}>{country.name}</option>
                            ))}
                        </Form.Select>
                    </Form.Group>
                    <Form.Label>Text</Form.Label>
                    <FloatingLabel className="mb-2" controlId="floatingTextarea2" label="Message">
                        <Form.Control
                        as="textarea"
                        placeholder="Leave a comment here"
                        style={{ height: '100px' }}
                        />
                    </FloatingLabel>
                    {
                        !errorMessage && <Alert variant={'danger'}>
                            {errorMessage}
                        </Alert>
                    }
                        <Col xs="auto" className="d-flex my-1 justify-content-end">                    
                            <Button className="btn btn-success  btn-block my-1 mb-3" onClick={() => console.log("girdi")}>Log in</Button>
                        </Col>
                </Form>
                </Row>
                </Container>
            )
}
