import React, { useContext } from 'react'
import { Link } from "react-router-dom";
import { Context } from '../context/Context';
import { Dropdown, Container, Row } from 'react-bootstrap';

export default function Header({setLoginModalShow}) {
    const {userInfo, setUserInfo} = useContext(Context);

    const logOutHandler = () => {
        setUserInfo({email:'', name:'', locale:'', password:''});
    }

    return (
        <Container>
            <Row>
            <nav className="navbar navbar-expand-lg navbar-dark navbar-custom fixed-top">
            <div className="container px-5">
                <Link className="navbar-brand" to="/">Scorp App</Link>
                <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarResponsive" aria-controls="navbarResponsive" aria-expanded="false" aria-label="Toggle navigation"><span className="navbar-toggler-icon"></span></button>
                <div className="collapse navbar-collapse" id="navbarResponsive">
                    <ul className="navbar-nav ms-auto">
                        <li className="nav-item">
                            <Link className="nav-link" to="/contact-us">Contact Us</Link>
                        </li>
                        {
                            userInfo?.name ?
                            <>
                            <Dropdown className="d-inline mx-2">
                            <Dropdown.Toggle id="dropdown-autoclose-true">
                            {userInfo?.email}
                            </Dropdown.Toggle>
                            <Dropdown.Menu className="mx-2">
                                <Dropdown.Item onClick={() => logOutHandler()}>
                                  Log Out
                                </Dropdown.Item>
                            </Dropdown.Menu>
                          </Dropdown>
                            </> : <li className="nav-item">
                                <button className="btn btn-secondary nav-link" onClick={() => setLoginModalShow(true)}>Log In</button>
                        </li>
                        }
                    </ul>
                </div>
            </div>
        </nav>

            </Row>
        </Container>
        )
}
