import React, { useContext } from 'react'
import { useState } from 'react';
import {
    Button,
    Modal,
    Alert,
    Form,
    Container
  } from "react-bootstrap";
import { Context } from '../context/Context';
import { countryList } from '../data/countryList';

export default function Login({hideHandler, show}) {

    let context = useContext(Context);

    const [errorMessage, setErrorMessage] = useState(null);

    const handleValidation = () => {
        let formValid = true;
        //Password
        if(!context.userInfo.password){
            setErrorMessage("Password cannot be empty");
            formValid = false;
        }

        //Email
        if(!context.userInfo.email){
          setErrorMessage("Email cannot be empty");
          formValid = false;
        }
        if(typeof context.userInfo.email !== "undefined"){
          let lastAtPos = context.userInfo.email.lastIndexOf('@');
          let lastDotPos = context.userInfo.email.lastIndexOf('.');
          if (!(lastAtPos < lastDotPos && lastAtPos > 0 && context.userInfo.email.indexOf('@@') === -1 && lastDotPos > 2 && (context.userInfo.email.length - lastDotPos) > 2)) {
            setErrorMessage("Email is not valid");
            formValid = false;
          }
        }

        //Name
        if(!context.userInfo.name){
            setErrorMessage("Name cannot be empty");
            formValid = false;
        }
        return formValid;
    }

    const submitHandler = () => {
        if(handleValidation()){
            setErrorMessage(null);
            hideHandler(false);
            console.log(context);
        }
    }
    return (
        <Modal show={show}
            size="lg"
            aria-labelledby="contained-modal-title-vcenter"
            centered
            >
            <Modal.Header className="d-flex align-items-center justify-content-center">
                <Modal.Title id="contained-modal-title-vcenter">
                Login
                </Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Container>
                <Form>
                    <Form.Group className="mb-3" controlId="formGroupName">
                        <Form.Label>Name</Form.Label>
                        <Form.Control type="text" placeholder="Name" onChange={e => {
                                    e.preventDefault();
                                    context.updateUserInfo({...context.userInfo, name:e.target.value})
                                }}/>
                    </Form.Group>
                    <Form.Group className="mb-3" controlId="formGroupEmail">
                        <Form.Label>Email</Form.Label>
                        <Form.Control type="email" placeholder="Enter email" onChange={e => {
                                    e.preventDefault();
                                    context.updateUserInfo({...context.userInfo, email:e.target.value})
                                }}/>
                    </Form.Group>
                    <Form.Group className="mb-3" controlId="formGroupLocale">
                    <Form.Label>Locale</Form.Label>
                        <Form.Select 
                                    defaultValue={context?.locale} 
                                    onChange={e => {
                                    e.preventDefault();
                                    context.updateUserInfo({...context.userInfo, locale:e.target.value})
                                }}>
                            {countryList.map((country) => (
                                <option key={country.id} value={country.id}>{country.name}</option>
                            ))}
                        </Form.Select>
                    </Form.Group>
                    <Form.Group className="mb-3" controlId="formGroupPassword">
                        <Form.Label>Password</Form.Label>
                        <Form.Control type="password" placeholder="Password" onChange={e => {
                                    e.preventDefault();
                                    context.updateUserInfo({...context.userInfo, password:e.target.value})
                                }}/>
                    </Form.Group>
                    {
                        errorMessage && <Alert variant={'danger'}>
                            {errorMessage}
                        </Alert>
                    }
                </Form>
                </Container>
            </Modal.Body>
            <Modal.Footer>     
                <Button type="submit" className="btn btn-danger btn-lg btn-block" onClick={() => hideHandler(false)}>Cancel</Button>
                <Button type="submit" className="btn btn-primary btn-lg btn-block" onClick={() => submitHandler()}>Log in</Button>
            </Modal.Footer>
            </Modal>
    )
}
